import React, { Component } from "react";
import Card from "./components/MickeyCard/Card";
import Header from "./components/Header";
import Score from "./components/Score";
import mickies from "./mickey.json";

class App extends Component {
  state = {
    clicks: 0,
    topScore: 0,
    message: "",
    wasClicked: []
  };

  clickListener = id => {
    if (this.state.wasClicked.indexOf(id) > -1) {
      console.log("clicked twice");
      this.setState({
        message: "You've Lost!"
      });
    } else {
      console.log(id);
      let updateWasClicked = [...this.state.wasClicked];
      updateWasClicked.push(id);
      this.setState({
        clicks: this.state.clicks + 1,
        wasClicked: updateWasClicked
      });
    }

    console.log("clicked");
  };

  handleClick() {
    this.setState({});
  }

  render() {
    console.log(this.state);
    return (
      <div>
        <Header />
        <Score clicks={this.state.clicks} message={this.state.message} />
        <div className="wrapper">
          {mickies.map(mickey => (
            <Card
              key={mickey.id}
              click={this.clickListener}
              image={mickey.image}
              id={mickey.id}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
