import React, { Component } from "react";
import Card from "./components/MickeyCard/Card";
import Header from "./components/Header";
import Score from "./components/Score";
import mickies from "./mickey.json";

class App extends Component {
  state = {
    clicks: 0,
    topScore: 0,
    message: "",
    wasClicked: []
  };

  clickListener = id => {
    console.log(id);
    let updateWasClicked = [...this.state.wasClicked];

    updateWasClicked.push(id);

    this.setState({
      clicks: this.state.clicks + 1,
      wasClicked: updateWasClicked
    });
    console.log("clicked");
  };

  handleClick() {
    this.setState({});
  }

  render() {
    return (
      <div>
        <Header />
        <Score clicks={this.state.clicks} />
        <div className="wrapper">
          {mickies.map(mickey => (
            <Card
              key={mickey.id}
              click={this.clickListener}
              image={mickey.image}
              id={mickey.id}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
