import React, { Component } from "react";
import Card from "./components/MickeyCard/Card";
import Header from "./components/Header";
import Score from "./components/Score";
import mickies from "./mickey.json";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      clicks: 0,
      topScore: 0,
      message: "",
      wasClicked: []
    };
  }

  clickListener() {
    this.setState({ clicks: this.state.clicks + 1 });
    console.log("clicked");
  }

  render() {
    return (
      <div>
        <Header />
        <Score clicks={this.state.clicks} />
        <div className="wrapper">
          {mickies.map(mickey => (
            <Card
              key={mickey.id}
              click={() => {
                console.log("hi");
                console.log(id);
                this.clickListener();
              }}
              image={mickey.image}
              id={mickey.id}
            />
          ))}
        </div>
      </div>
    );
  }
}

export default App;
